INSERT INTO `photo_class` VALUES ('26', '城南中学九（6）班', '1', '<p photo_class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;color:#000000;font-size:14px;\">“精彩六班，气宇轩昂；猎鹰展翅，超越梦想。”</span><span style=\"font-family:Arial;color:#FF00FF;font-size:14px;\">&nbsp;</span><span photo_class=\"16\" style=\"font-family:宋体;color:#000000;font-weight:normal;font-size:14px;\">这</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">是一个</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">团结奋进</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">、和谐</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">幸福</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">的</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">大家庭</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">。</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">记住我们的班风：</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">“</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">安全、品质、健康、学习</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">”</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">，莫忘我们的班训：</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">“</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">为善小、积跬步、松静匀乐成大器</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">”</span><span style=\"font-family:宋体;color:#000000;font-size:14px;\">。美丽的校园洒满我们辛勤的汗水也荡漾着我们灿烂的笑容！怀念那些师生一起拼搏的岁月，我们硕果累累</span><span style=\"font-family:Arial;color:#000000;font-size:14px;\">……</span> \r\n</p>', 'music/1465961006649.mp3', null, '2016-06-15 00:00:00', '2017-05-21 23:26:12', null, null, null, null, null, '1', '1', '632');
