INSERT INTO `photo_class` VALUES ('28', '桐梓县第五中学九（2）班', '1', '&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 桐梓县第五中学是2004年9月经县委、县人民政府研究决定成立的一所县直属初级中学。五中的前身为桐梓二中初中部，位于县城西门武胜路虎峰山下。学校环境清幽，古木参天。两年前新建了教学楼和运动场，现有图书、实验、计算机、多媒体等专用教室，有教职工118人，其中高级教师18人、一级教师52人，具有本科学历的教师有 62人，有市中考命题人才库教师7 人，县统一考试命题教师18人，县教研室兼职教研员 11人，学生2600余人，37个教学班。一支热爱教育事业、师德高尚、业务精湛、富有创新精神和实践能力的教职员工队伍，努力打造博爱笃教、务本创新、团结和谐、荣辱不惊、勤奋拼搏、奋力争先的五中精神。', 'music/1466045909602.mp3', null, '2016-06-16 00:00:00', '2017-05-21 23:26:12', null, null, null, null, null, '1', '1', '771');
