INSERT INTO `photo_class` VALUES ('42', '贵州省仁怀县师范学校', '1', '&nbsp; &nbsp; &nbsp; &nbsp;回眸三十载，共赏一窗谊<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;年年花相似，岁岁人不同。三十年弹指一挥间，岁月的印痕，深刻在每一个熟悉的面孔，令人感动唏嘘。<br />\r\n<p>\r\n	&nbsp;回眸三十年，足以让我们在滚滚红尘中体味人生百味，坎坷与平淡，艰辛与苦涩，幸福与甘甜，失败与成功，怡然绽放无尽的感怀。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp;岁月沧桑，唯同学情谊一如尘封有年的佳酿，越品越香，越品越醇，翻阅<span style=\"line-height:1.5;\">每一页精彩，检点那些刻骨铭心的故事，无一不是横亘两个世纪的华章 &nbsp;乐典，</span><span style=\"line-height:1.5;\">承载的都是彼此的牵挂与思念，记录的都是一路放歌穿越时空的妙熳。</span>\r\n</p>\r\n&nbsp; &nbsp; &nbsp; &nbsp;岁月如歌，流水不腐，血肉亲情，友谊永恒。让记忆就这样停留在过去的光影中，就这样装点着我们人生的绚丽于多彩，就这样快乐着你的快 &nbsp;乐，幸福着你的幸福。<br />\r\n&nbsp; &nbsp; &nbsp; 一次短暂的相聚，一次衷心的倾诉，一次同学就是一生的念想，终身的承诺......一次就足够了。<br />\r\n&nbsp; &nbsp; &nbsp; 互道一声珍重，愿我们到老的时候，依然年轻。<br />', 'music/1470903122801.mp3', null, '2016-07-25 00:00:00', '2017-05-21 23:26:12', null, null, null, 'Sn0e1BRHTkAzrCnMuGU9mw==', null, '1', '1', '1412');
