mapper.select:
		select top 

            ${limit}10
         marry.id, marry.epc, marry.io_type, marry.io_date, marry.rfid_id from (select row_number() over(

             order by ${sidx} ${order} 
			 order by id desc 
        ) as rownumber,

		from(SELECT theio.id , theio.epc, theio.io_type, theio.io_date, theio.rfid_id,theio.student_id FROM 
		io as theio left join student_epc se on theio.epc=se.epc left join student as stu on stu.id=se.student_id where stu.id= #{studentId}) as harry) as marry where rownumber > 

             ${offset} 
			0

mapper.update:
update io
epc = #{epc}, io_type = #{ioType}, io_date = #{ioDate}, student_id = #{studentId}, rfid_id = #{rfidId}
where id = #{id}
