mapper.select:
select t3.student_id as id from io t3,(
	select t1.* from student t1,(
	select id from class where school_id = #{schoolId}
	) t2 where t1.class_id = t2.id) t4 where t3.student_id = t4.id and t3.io_type='进'
	and t3.id in(
	select MAX(t3.id) as id from io t3,(
	select t1.id from student t1,(
	select id from class where school_id = #{schoolId}
	) t2 where t1.class_id = t2.id
	) t4
	where t3.student_id = t4.id GROUP BY t3.student_id 
	)
	GROUP BY t3.student_id ORDER BY t3.student_id

select t1.id from student t1,(
	select id from class where school_id = #{schoolId}
	) t2 where t1.class_id = t2.id

        select top 

            ${limit}10

        marry.id, marry.epc, marry.io_type, marry.io_date, marry.rfid_id,marry.cameraNo,marry.verification_code ,marry.serial_number from (select row_number() over(

             order by ${sidx} ${order} 
			 order by io_date desc 
        ) as rownumber,

		from(SELECT theio.id , theio.epc, theio.io_type, theio.io_date, theio.rfid_id,theio.student_id,theio.cameraNo,theio.verification_code ,theio.serial_number FROM 
		io as theio where student_id= #{studentId}) as harry) as marry where rownumber > 

             ${offset} 
			0

mapper.update:
update io
epc = #{epc}, io_type = #{ioType}, io_date = #{ioDate}, student_id = #{studentId}, rfid_id = #{rfidId}
where id = #{id}
